import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  state = {};

  //
  // componentDidMount() {
  //   return synth;
  // }

  clickHandler = () => {
    let synth = new Tone.GrainPlayer("./samples/modular rock.wav").toMaster();
    synth.start();
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
