import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  state = {};

  clickHandler = () => {
    // var buffer = new Tone.Buffer(
    //   "./samples/modular rock.mp3"
    //   //the buffer is now available.
    // );
    // let grain = new Tone.GrainPlayer({
    //   url: buffer,
    //   loop: true,
    //   grainSize: 0.1,
    //   overlap: 0.05
    // })
    //   .toMaster()
    //   .start();

    var player = new Tone.Player("./samples/modular rock.mp3").toMaster();
    //play as soon as the buffer is loaded
    player.autostart = true;
  };
  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
