import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  state = {};
  componentDidMount() {
    let synth = new Tone.GrainPlayer("./samples/modular rock.wav").toMaster();
    return synth;
  }

  clickHandler = synth => {
    return synth;
  };

  render(synth) {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
