import React from "react";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  State = {};

  kick = new Tone.MembraneSynth({
    pitchDecay: 0.07,
    octaves: 5,
    oscillator: {
      type: "sine"
    },
    envelope: {
      attack: 0.001,
      decay: 0.2,
      sustain: 0.002,
      release: 1,
      attackCurve: "exponential"
    }
  }).toMaster();

  handleClick = () => {
    console.log("kick");
    this.kick.triggerAttackRelease("c1", "8n", 0.8);
  };
  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
