import React from "react";
import Step from "./Step";

class StepSequencer extends React.Component {
  state = {
    arrayLength: 16
  };

  render() {
    let arr = Array(this.state.arrayLength);
    return (
      <div
        style={{
          flex: 1,
          flexDirection: "row"
        }}
      >
        {arr.map(step => {
          return <Step key={step} />;
        })}
      </div>
    );
  }
}

export default StepSequencer;
