import React from "react";
import Kick from "./Kick";
import Snare from "./Snare";
import Hihat1 from "./Hihat1";
import Hihat2 from "./Hihat2";

class InstrumentsContainer extends React.Component {
  state = {
    kickButton: false,
    snareButton: false,
    hh1Button: false,
    hh2Button: false
  };

  clickHandlerKickButton = e => {
    console.log("event", e.target);
    this.setState({
      kickButton: !this.state.kickButton,
      snareButton: false,
      hh1Button: false,
      hh2Button: false
    });
  };

  clickHandlerSnareButton = e => {
    console.log("event", e.target);
    this.setState({
      snareButton: !this.state.snareButton,
      kickButton: false,
      hh1Button: false,
      hh2Button: false
    });
  };

  clickHandlerHh1Button = e => {
    console.log("event", e.target);
    this.setState({
      hh1Button: !this.state.hh1Button,
      kickButton: false,
      snareButton: false,
      hh2Button: false
    });
  };

  render() {
    return (
      <div>
        <h1>
          Instruments Container
          <br />
          <button type="button" onClick={this.clickHandlerKickButton}>
            Kick Params
          </button>
          <button type="button" onClick={this.clickHandlerSnareButton}>
            Snare Params
          </button>
          <button type="button" onClick={this.clickHandlerHh1Button}>
            Closed Hi hat Params
          </button>
          <div>
            <Kick time={this.props} />
            <Snare />
            <Hihat1 />
            <Hihat2 />
          </div>
        </h1>
      </div>
    );
  }
}

export default InstrumentsContainer;
