import React from "react";

class Step extends React.Component {
  render() {
    let style = {
      width: "30px",
      height: "60px",

      backgroundColor: this.props.clicked ? "#2AC7DC" : "#CBCBCB",
      borderRadius: "10px",
      margin: 5,
      display: "inline-block"
    };

    return (
      <div
        data-id={this.props.id}
        style={style}
        onMouseDown={this.props.clickHandler}
      ></div>
    );
  }
}

export default Step;
