import React from "react";
import "../App.css";
import Tone from "tone";
import sample from "../samples/modular rock.mp3";

class Grain extends React.Component {
  state = {
    pan: -0.3
  };

  clickHandler = () => {
    // let hithatPan = new Tone.PanVol({
    //   pan: 0.35
    // }).toMaster();
    let PanVol = new Tone.PanVol({
      pan: -0.5,
      volume: -10,
      mute: false
    }).toMaster();

    let granular = new Tone.Player(sample, {
      onload: Tone.noOp,
      playbackRate: 1,
      loop: false,
      autostart: false,
      loopStart: 0,
      loopEnd: 0,
      reverse: false,
      fadeIn: 0,
      fadeOut: 0
    }).connect(PanVol);
    granular.autostart = true;
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Grain
        </button>
      </div>
    );
  }
}

export default Grain;
