import React from "react";
import Kick from "./Kick";
import Snare from "./Snare";
import Hihat1 from "./Hihat1";
import Hihat2 from "./Hihat2";

class InstrumentsContainer extends React.Component {
  state = {
    kickButton: false,
    snareButton: false,
    hh1Button: false,
    hh2Button: false
  };

  clickHandlerKickButton = e => {
    console.log("event", e.target);
    this.setState({
      kickButton: !this.state.micButton,
      snareButton: false,
      hh1Button: false,
      hh2Button: false
    });
  };

  render() {
    return (
      <div>
        <h1>
          Instruments Container
          <button type="button" onClick={this.buttonHandler}>
            Click Me!
          </button>
          <div>
            <Kick time={this.props} />
            <Snare />
            <Hihat1 />
            <Hihat2 />
          </div>
        </h1>
      </div>
    );
  }
}

export default InstrumentsContainer;
