import React from "react";
import Tone from "tone";
import { Knob } from "react-rotary-knob";
import * as skins from "react-rotary-knob-skin-pack";

const Kick = props => {
  let kickHandler = () => {
    let kick = new Tone.MembraneSynth({
      pitchDecay: props.kickPitchDecay,
      octaves: 5,
      oscillator: {
        type: "sine"
      },
      envelope: {
        attack: props.kickAttack,
        decay: props.kickDecay,
        sustain: props.kickSustain,
        release: props.kickRelease,
        attackCurve: "exponential"
      }
    }).toMaster();
    kick.triggerAttackRelease("c1", "8n");
  };

  // attackHandler = val => {
  //   this.setState({ attack: val });
  //   // console.log(this.state.attack);
  // };

  // decayHandler = val => {
  //   this.setState({ decay: val });
  //   // console.log(this.state.decay);
  // };

  // sustainHandler = val => {
  //   this.setState({ sustain: val });
  //   // console.log(this.state.sustain);
  // };

  // releaseHandler = val => {
  //   this.setState({ release: val });
  //   // console.log(this.state.release);
  // };

  // pitchDecayHandler = val => {
  //   this.setState({ pitchDecay: val });
  //   console.log(this.state.release);
  // };

  return (
    <div className="App">
      <button type="button" onClick={this.kickHandler}>
        Kick
      </button>

      {props.knobs === "envelope" ? (
        <div>
          {/* Attack Knob:: */}

          <Knob
            skin={skins.s16}
            unlockDistance={30}
            onChange={props.kickAttackHandler}
            min={0.0}
            max={0.09}
            clampMax={180}
            rotateDegrees={270}
            value={props.kickAttack}
          />

          {/* Decay Knob:: */}
          <Knob
            skin={skins.s16}
            unlockDistance={30}
            onChange={props.kickDecayHandler}
            min={0}
            max={1}
            clampMax={180}
            rotateDegrees={270}
            value={props.kickDecay}
          />
          {/* Sustain Knob:: */}
          <Knob
            skin={skins.s16}
            unlockDistance={30}
            onChange={props.kickSustainHandler}
            min={0.0}
            max={0.5}
            clampMax={180}
            rotateDegrees={270}
            value={props.kickSustain}
          />

          {/* Release Knob:: */}
          <Knob
            skin={skins.s16}
            unlockDistance={30}
            onChange={props.kickReleaseHandler}
            min={0.0}
            max={2}
            clampMax={180}
            rotateDegrees={270}
            value={props.kickRelease}
          />
        </div>
      ) : null}

      {this.props.knobs === "osc" ? (
        <div>
          {/* Pitch Decay Knob:: */}

          <Knob
            skin={skins.s16}
            unlockDistance={30}
            onChange={props.kickPitchDecayHandler}
            min={0.0}
            max={0.6}
            clampMax={180}
            rotateDegrees={270}
            value={props.kickPitchDecay}
          />

          {/* Decay Knob:: */}
          {/* <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.decayHandler}
              min={0}
              max={1}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.decay}
            /> */}
          {/* Sustain Knob:: */}
          {/* <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.sustainHandler}
              min={0.0}
              max={0.5}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.sustain}
            /> */}

          {/* Release Knob:: */}
        </div>
      ) : null}
    </div>
  );
};

export default Kick;
