import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";

class Kick extends React.Component {
  state = {};

  //
  // componentDidMount() {
  //   return synth;
  // }

  clickHandler = () => {
    let kick = new Tone.MembraneSynth({
      pitchDecay: 0.07,
      octaves: 5,
      oscillator: {
        type: "sine"
      },
      envelope: {
        attack: 0.001,
        decay: 0.2,
        sustain: 0.002,
        release: 1,
        attackCurve: "exponential"
      }
    }).toMaster();
    kick.triggerAttackRelease("c1", "8n");
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Kick
        </button>
      </div>
    );
  }
}

export default App;
