import React from "react";
import Kick from "../components/Kick";
import StepSequencer from "../components/StepSequencer";

class SequencerContainer extends React.Component {
  state = {
    reset: 0
  };

  resetHandler = () => {
    this.setState({
      reset: 0
    });
  };
  render() {
    return (
      <div>
        <button onClick={this.resetHandler} type="button">
          reset Sequencer
        </button>
        <br />
        {/* Kick sequencer */}
        <StepSequencer
          triggerHandler={this.props.kickHandler}
          time={this.props.time}
          reset={this.state.reset}
        />

        {/* Snare sequencer */}
        <StepSequencer
          triggerHandler={this.props.snareHandler}
          time={this.props.time}
          reset={this.state.reset}
        />
      </div>
    );
  }
}

export default SequencerContainer;
