import React from "react";
import Step from "./Step";

class StepSequencer extends React.Component {
  state = {
    arrayLength: 16
  };

  render() {
    // let arr = [1, 2, 3, 4];

    let arr = () => {
      let data = [];
      for (var i = 0; i < this.state.arrayLength; i++) {
        return data.push(true);
      }
    };

    // new Array(this.state.arrayLength);

    return (
      <div
        style={{
          flex: 1,
          flexDirection: "row"
        }}
      >
        {arr.map((x, i) => {
          return <Step />;
        })}
      </div>
    );
  }
}

export default StepSequencer;
