import React from "react";
import "../App.css";
import Tone from "tone";

class Kick extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.kick = new Tone.MembraneSynth({
      pitchDecay: 0.07,
      octaves: 5,
      oscillator: {
        type: "sine"
      },
      envelope: {
        attack: 0.001,
        decay: 0.2,
        sustain: 0.002,
        release: 1,
        attackCurve: "exponential"
      }
    }).toMaster();
  }

  kickHandler = () => {
    this.kick.triggerAttackRelease("c1", "8n");
  };

  render() {
    return (
      <div className="App">
        <button
          onClick={this.props.time[1] === 1 ? this.kickHandler() : null}
          type="button"
        >
          Kick
        </button>
      </div>
    );
  }
}

export default Kick;
