import React from "react";
class Step extends React.Component {
  state = { clicked: false };

  clickHandler = () => {
    this.setState({
      clicked: !this.state.clicked
    });
    console.log(this.state.clicked);
  };
  render() {
    let style = {
      width: "3em",
      height: "5em",
      backgroundColor: this.state.clicked ? "#2AC7DC" : "#CBCBCB",
      borderRadius: "10px",
      margin: 5,
      display: "inline-block"
    };

    return <div style={style} onClick={this.clickHandler}></div>;
  }
}

export default Step;
