import React from "react";

import Snare from "../components/Snare";
import Hihat1 from "../components/Hihat1";
import Hihat2 from "../components/Hihat2";
import KickEnvelopeContainer from "./KickEnvelopeContainer";
import OscControlsContainer from "./OscControlsContainer";
import SequencerContainer from "./SequencerContainer";

class InstrumentsContainer extends React.Component {
  state = {
    kickButton: false,
    snareButton: false,
    hh1Button: false,
    hh2Button: false,

    attack: 0.001,
    decay: 0.2,
    sustain: 0.002,
    release: 1,
    pitchDecay: 0.07
  };
  /// Sound settings methods:::
  clickHandlerKickButton = e => {
    console.log("event", e.target);
    this.setState({
      kickButton: true,
      snareButton: false,
      hh1Button: false,
      hh2Button: false
    });
  };

  clickHandlerSnareButton = e => {
    console.log("event", e.target);
    this.setState({
      snareButton: true,
      kickButton: false,
      hh1Button: false,
      hh2Button: false
    });
  };

  clickHandlerHh1Button = e => {
    console.log("event", e.target);
    this.setState({
      hh1Button: true,
      kickButton: false,
      snareButton: false,
      hh2Button: false
    });
  };

  clickHandlerHh2Button = e => {
    console.log("event", e.target);
    this.setState({
      hh2Button: true,
      kickButton: false,
      snareButton: false,
      hh1Button: false
    });
  };

  /// Kick methods::::

  attackHandler = val => {
    this.setState({ attack: val });
    // console.log(this.state.attack);
  };

  decayHandler = val => {
    this.setState({ decay: val });
    // console.log(this.state.decay);
  };

  sustainHandler = val => {
    this.setState({ sustain: val });
    // console.log(this.state.sustain);
  };

  releaseHandler = val => {
    this.setState({ release: val });
    // console.log(this.state.release);
  };

  pitchDecayHandler = val => {
    this.setState({ pitchDecay: val });
    console.log(this.state.release);
  };

  render() {
    return (
      <div>
        <h1>
          Instruments Container
          <br />
          <button type="button" onClick={this.clickHandlerKickButton}>
            Kick Params
          </button>
          <button type="button" onClick={this.clickHandlerSnareButton}>
            Snare Params
          </button>
          <button type="button" onClick={this.clickHandlerHh1Button}>
            Closed Hi hat Params
          </button>
          <button type="button" onClick={this.clickHandlerHh2Button}>
            Open Hi hat Params
          </button>
          <div>
            {this.state.kickButton ? (
              <div className="container">
                <div>
                  <KickEnvelopeContainer kickParams={this.state.kickParams} />
                </div>
                <div>
                  <OscControlsContainer />
                </div>
              </div>
            ) : null}
            {/* {this.state.kickButton ? <Kick knobs={true} /> : null} */}
            {this.state.snareButton ? <Snare /> : null}
            {this.state.hh1Button ? <Hihat1 /> : null}
            {this.state.hh2Button ? <Hihat2 /> : null}
            {/* <Kick time={this.props} />
            <Snare />
            <Hihat1 />
            <Hihat2 /> */}

            <SequencerContainer />
          </div>
        </h1>
      </div>
    );
  }
}

export default InstrumentsContainer;
