import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";
import Granular from "granular-js";

class App extends React.Component {
  state = {};

  clickHandler = () => {
    var buffer = new Tone.Buffer("./samples/modular rock.mp3", function() {
      //the buffer is now available.
      var buff = buffer.get();
    });
    let grain = new Tone.GrainPlayer({
      // url: "../modular rock.mp3",
      loop: true,
      grainSize: 0.1,
      overlap: 0.05
    })
      .toMaster()
      .start();
  };
  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
