import React from "react";
import "../App.css";
import Tone from "tone";
import { Knob } from "react-rotary-knob";
import * as skins from "react-rotary-knob-skin-pack";

class Kick extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      attack: 0.001,
      decay: 0.2,
      sustain: 0.002,
      release: 1
    };
    this.kick = new Tone.MembraneSynth({
      pitchDecay: 0.07,
      octaves: 5,
      oscillator: {
        type: "sine"
      },
      envelope: {
        attack: this.state.attack,
        decay: this.state.decay,
        sustain: this.state.sustain,
        release: this.state.release,
        attackCurve: "exponential"
      }
    }).toMaster();
  }

  kickHandler = () => {
    this.kick.triggerAttackRelease("c1", "8n");
  };

  attackHandler = val => {
    this.setState({ attack: val });
    console.log(this.state.attack);
  };
  // attackHandler = val => {
  //   this.setState({ attack: val });
  //   console.log(this.state.attack);
  // };

  render() {
    return (
      <div className="App">
        <button type="button" onClick={this.kickHandler}>
          Kick
        </button>

        {this.props.knobs === "envelope" ? (
          <div>
            <Knob
              skin={skins.s16}
              unlockDistance={50}
              onChange={this.attackHandler}
              // min={0.001}
              // max={1}
              value={this.state.attack}
            />

            {/* <Knob
              skin={skins.s16}
              unlockDistance={50}
              onChange={this.attackHandler}
              min={0.001}
              max={1}
              value={this.state.attack}
            />
            <Knob skin={skins.s16} unlockDistance={50} />
            <Knob skin={skins.s16} unlockDistance={50} /> */}
          </div>
        ) : null}
      </div>
    );
  }
}

export default Kick;
