import React from "react";
import "../App.css";
import Tone from "tone";
import { Knob } from "react-rotary-knob";
import * as skins from "react-rotary-knob-skin-pack";

class Snare extends React.Component {
  state = {
    pan: -0.3
  };

  clickHandler = () => {
    let PanVol = new Tone.PanVol({
      pan: this.state.pan,
      volume: -0,
      mute: false
    }).toMaster();
    let snare = new Tone.NoiseSynth({
      noise: {
        type: "brown"
      },
      envelope: {
        attack: 0,
        decay: 0.07,
        sustain: 0.01
      }
    }).connect(PanVol);
    snare.triggerAttackRelease("8n");
  };

  changeValuePan = val => {
    this.setState({ pan: val });
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Snare
        </button>
        <Knob
          skin={skins.s12}
          onChange={this.changeValuePan}
          min={3}
          max={1}
          value={this.state.pan}
        />
      </div>
    );
  }
}

export default Snare;
