import React from "react";

class Step extends React.Component {
  state = {
    mouse: false
  };

  mouseChange = () => {
    this.setState({
      mouse: !this.state.mouse
    });
    console.log(this.state.mouse);
  };
  handleMouse = e => {
    console.log(e.type);
    if (this.state.mouse) {
      this.props.clickHandler(e);
      console.log(this.state.mouse);
    }
  };

  render() {
    let style = {
      width: "35px",
      height: "60px",

      backgroundColor: this.props.clicked ? "#6b6e6e" : "#CBCBCB",
      border: this.props.borderColor
        ? `${this.props.color}`
        : "3px solid white",
      borderRadius: "10px",
      margin: 5,
      display: "inline-block"
    };

    return (
      <div
        data-id={this.props.id}
        style={style}
        onClick={this.mouseChange}
        onMouseOver={this.handleMouse}
      ></div>
    );
  }
}

export default Step;
