import React from "react";
import { Knob } from "react-rotary-knob";
import * as skins from "react-rotary-knob-skin-pack";
class Step extends React.Component {
  state = { clicked: false };

  clickHandler = () => {
    this.setState({
      clicked: !this.state.clicked
    });
  };
  render() {
    let style = {
      width: "30px",
      height: "60px",
      backgroundColor: this.state.clicked ? "#2AC7DC" : "#CBCBCB",
      borderRadius: "10px",
      margin: 5,
      display: "inline-block"
    };

    return (
      <div style={style} onClick={this.clickHandler}>
        <div className="knob">
          <small className="knobText">Steps</small>
          <Knob
            skin={skins.s16}
            unlockDistance={30}
            onChange={this.stepHandler}
            min={1}
            max={16}
            clampMax={180}
            rotateDegrees={270}
            value={this.state.val}
          />
        </div>
      </div>
    );
  }
}

export default Step;
