import React from "react";
import "../App.css";
import Tone from "tone";
import { Knob } from "react-rotary-knob";
import * as skins from "react-rotary-knob-skin-pack";

class Kick extends React.Component {
  state = {
    attack: 0.001,
    decay: 0.2,
    sustain: 0.002,
    release: 1
  };
  kickHandler = () => {
    let kick = new Tone.MembraneSynth({
      pitchDecay: 0.07,
      octaves: 5,
      oscillator: {
        type: "sine"
      },
      envelope: {
        attack: this.state.attack,
        decay: this.state.decay,
        sustain: this.state.sustain,
        release: this.state.release,
        attackCurve: "exponential"
      }
    }).toMaster();
    kick.triggerAttackRelease("c1", "8n");
  };

  attackHandler = val => {
    this.setState({ attack: val });
    // console.log(this.state.attack);
  };

  decayHandler = val => {
    this.setState({ decay: val });
    // console.log(this.state.decay);
  };

  sustainHandler = val => {
    this.setState({ sustain: val });
    // console.log(this.state.sustain);
  };

  releaseHandler = val => {
    this.setState({ release: val });
    console.log(this.state.release);
  };

  render() {
    return (
      <div className="App">
        <button type="button" onClick={this.kickHandler}>
          Kick
        </button>

        {this.props.knobs === "envelope" ? (
          <div>
            {/* Attack Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.attackHandler}
              min={0.0}
              max={0.09}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.attack}
            />
            {/* Decay Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.decayHandler}
              min={0}
              max={1}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.decay}
            />
            {/* Sustain Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.sustainHandler}
              min={0.0}
              max={0.5}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.sustain}
            />

            {/* Release Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.releaseHandler}
              min={0.0}
              max={1}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.release}
            />
          </div>
        ) : null}
      </div>
    );
  }
}

export default Kick;
