import React from "react";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  state = {};

  //
  componentDidMount() {
    let kick = new Tone.MembraneSynth({
      pitchDecay: 0.07,
      octaves: 5,
      oscillator: {
        type: "sine"
      },
      envelope: {
        attack: 0.001,
        decay: 0.2,
        sustain: 0.002,
        release: 1,
        attackCurve: "exponential"
      }
    }).toMaster();
    return kick;
  }

  clickHandler = kick => {
    kick.triggerAttackRelease("c1", "8n");
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
