import React from "react";
import { Transport, Loop } from "tone";
import InstrumentsContainer from "./InstrumentsContainer";

class Trans extends React.Component {
  constructor(props) {
    super(props);

    Transport.bpm.value = 125;
    Transport.schedule(this.startLoop, "0");
    Transport.loop = true;
    Transport.loopEnd = "1m";
  }

  startLoop = time => {
    console.log("start loop", time);
    let counter = 0;
    let currentBeat = Transport.position.split(":");
    console.log(currentBeat);
    counter = (counter + 1) % 16;
  };
  transportHandler = () => {
    Transport.start();
  };

  render() {
    return (
      <div>
        <button onClick={this.transportHandler} type="button">
          Transport
        </button>
        <InstrumentsContainer time={this.startLoop} />
      </div>
    );
  }
}

export default Trans;
