import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  state = {};

  //
  // componentDidMount() {
  //   return synth;
  // }

  clickHandler = () => {
    let synth = new Tone.GrainPlayer("./samples/modular rock.wav", {
      onload: Tone.noOp,
      overlap: 0.1,
      grainSize: 0.2,
      playbackRate: 1,
      detune: 0,
      loop: false,
      loopStart: 0,
      loopEnd: 0,
      reverse: false
    }).toMaster();
    // synth.triggerAttackRelease("C4", "8n");
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
