import React from "react";
import Step from "./Step";

class StepSequencer extends React.Component {
  state = {
    arrayLength: 16
  };

  createArray = () => {
    let data = [];
    for (var i = 0; i < this.state.arrayLength; i++) {
      return data.push(true);
    }
  };

  render(data) {
    let arr = new Array(this.state.arrayLength);

    return (
      <div
        style={{
          flex: 1,
          flexDirection: "row"
        }}
      >
        {data.forEach((x, i) => {
          return <Step />;
        })}
      </div>
    );
  }
}

export default StepSequencer;
