  // decayHandler = val => {
  //   this.setState({ decay: val });
  //   // console.log(this.state.decay);
  // };

  // sustainHandler = val => {
  //   this.setState({ sustain: val });
  //   // console.log(this.state.sustain);
  // };

  // releaseHandler = val => {
  //   this.setState({ release: val });
  //   // console.log(this.state.release);
  // };

  // pitchDecayHandler = val => {
  //   this.setState({ pitchDecay: val });
  //   console.log(this.state.release);
  // };

  render() {
    return (
      <div className="App">
        <button type="button" onClick={this.props.kickHandler}>
          Kick
        </button>

        {this.props.knobs === "envelope" ? (
          <div>
            {/* Attack Knob:: */}

            <div>
              <Knob
                skin={skins.s16}
                unlockDistance={30}
                onChange={this.props.kickAttackHandler}
                min={0.0}
                max={0.09}
                clampMax={180}
                rotateDegrees={270}
                value={this.props.kickAttack}
              />
              <small>Attack</small>
            </div>

            {/* Decay Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.props.kickDecayHandler}
              min={0}
              max={1}
              clampMax={180}
              rotateDegrees={270}
              value={this.props.kickDecay}
            />
            {/* Sustain Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.props.kickSustainHandler}
              min={0.0}
              max={0.5}
              clampMax={180}
              rotateDegrees={270}
              value={this.props.kickSustain}
            />

            {/* Release Knob:: */}
            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.props.kickReleaseHandler}
              min={0.0}
              max={2}
              clampMax={180}
              rotateDegrees={270}
              value={this.props.kickRelease}
            />
          </div>
        ) : null}

        {this.props.knobs === "osc" ? (
          <div>
            {/* Pitch Decay Knob:: */}

            <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.props.kickPitchDecayHandler}
              min={0.0}
              max={0.6}
              clampMax={180}
              rotateDegrees={270}
              value={this.props.kickPitchDecay}
            />

            {/* Decay Knob:: */}
            {/* <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.decayHandler}
              min={0}
              max={1}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.decay}
            /> */}
            {/* Sustain Knob:: */}
            {/* <Knob
              skin={skins.s16}
              unlockDistance={30}
              onChange={this.sustainHandler}
              min={0.0}
              max={0.5}
              clampMax={180}
              rotateDegrees={270}
              value={this.state.sustain}
            /> */}

            {/* Release Knob:: */}
          </div>
        ) : null}
      </div>
    );
  }
}

export default Kick;
