import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Tone from "tone";

class App extends React.Component {
  state = {};

  // GrainPlayer("./samples/modular rock.wav")
  componentDidMount() {
    let synth = new Tone.MonoSynth({
      oscillator: {
        type: "square"
      },
      envelope: {
        attack: 0.1
      }
    }).toMaster();
    return synth;
  }

  clickHandler = synth => {
    return synth.triggerAttackRelease("C4", "8n");
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Click Me!
        </button>
      </div>
    );
  }
}

export default App;
