import React from "react";
import "../App.css";
import Tone from "tone";

class Kick extends React.Component {
  state = {
    pan: -0.3
  };

  //
  // componentDidMount() {
  //   return synth;
  // }

  clickHandler = () => {
    let snarePan = new Tone.PanVol({
      pan: this.state.pan
    }).toMaster();
    let snare = new Tone.NoiseSynth({
      noise: {
        type: "brown"
      },
      envelope: {
        attack: 0,
        decay: 0.05,
        sustain: 0.008
      }
    }).connect(snarePan);
    snare.triggerAttackRelease("8n");
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.clickHandler} type="button">
          Snare
        </button>
      </div>
    );
  }
}

export default Kick;
