import React from "react";
import "../App.css";
import Tone from "tone";
import { Knob } from "react-rotary-knob";
import * as skins from "react-rotary-knob-skin-pack";

class Snare extends React.Component {
  // state = {
  //   pan: 0
  // };

  // clickHandler = () => {
  //   // let PanVol = new Tone.PanVol({
  //   //   pan: this.state.pan,
  //   //   volume: -0,
  //   //   mute: false
  //   // }).toMaster();
  //   // let snare = new Tone.NoiseSynth({
  //   //   noise: {
  //   //     type: "brown"
  //   //   },
  //   //   envelope: {
  //   //     attack: 0,
  //   //     decay: 0.07,
  //   //     sustain: 0.01
  //   //   }
  //   // }).connect(PanVol);
  //   snare.triggerAttackRelease("8n");
  // };

  // changeValuePan = val => {
  //   this.setState({ pan: val });
  //   console.log(this.state.pan);
  // };

  render() {
    return (
      <div className="App">
        {this.props.knobs === "envelope" ? (
          <div>
            {/* Attack Knob:: */}

            <div className="knob">
              <small className="knobText">Attack</small>
              <Knob
                skin={skins.s16}
                unlockDistance={30}
                onChange={this.props.snareAttackHandler}
                min={0.0}
                max={0.05}
                clampMax={180}
                rotateDegrees={270}
                value={this.props.snareAttack}
              />
            </div>

            {/* Decay Knob:: */}
            <div className="knob">
              <small className="knobText">Decay</small>
              <Knob
                skin={skins.s16}
                unlockDistance={30}
                onChange={this.props.snareDecayHandler}
                min={0}
                max={0.4}
                clampMax={180}
                rotateDegrees={270}
                value={this.props.snareDecay}
              />
            </div>
            {/* Sustain Knob:: */}
            <div className="knob">
              <small className="knobText">Sustain</small>
              <Knob
                skin={skins.s16}
                unlockDistance={30}
                onChange={this.props.snareSustainHandler}
                min={0.0}
                max={0.1}
                clampMax={180}
                rotateDegrees={270}
                value={this.props.snareSustain}
              />
            </div>
          </div>
        ) : null}

        {this.props.knobs === "noise" ? (
          <div>
            {/* Pitch Decay Knob:: */}

            <div className="knob">
              <small className="knobText">Volume</small>
              <Knob
                skin={skins.s16}
                unlockDistance={30}
                onChange={this.props.snareVolume}
                min={0.0}
                max={0.6}
                clampMax={180}
                rotateDegrees={270}
                value={this.props.kickPitchDecay}
              />
            </div>

            {/* Oscillator type menu */}
            <div>
              <select className="knob" onChange={this.props.kickOscTypeHandler}>
                <option value="sine">sine</option>
                <option value="square">square</option>
                <option value="triangle">triangle</option>
                <option value="sawtooth">sawtooth</option>
              </select>
            </div>

            {/* Note Knob::*/}

            <div className="knob">
              <small className="knobText">Frequency</small>
              <Knob
                skin={skins.s16}
                unlockDistance={30}
                onChange={this.props.kickNoteHandler}
                min={30}
                max={120}
                clampMax={180}
                rotateDegrees={270}
                value={this.props.kickNote}
              />
            </div>
          </div>
        ) : null}
      </div>
    );
  }
}

export default Snare;
